-- Quer: Total sales per event_type

WITH SalesByDay AS (
    SELECT
        fs.store_id,  -- Store ID from the fact_sales table
        dd.event_name_1,  -- Event name from the date dimension
        fs.sell_price,
        CASE
            WHEN dd.d = 'd_1' THEN fs.d_1
            WHEN dd.d = 'd_2' THEN fs.d_2
            WHEN dd.d = 'd_3' THEN fs.d_3
            -- Continue for all columns up to d_1000
            WHEN dd.d = 'd_1000' THEN fs.d_1000
        END AS sales_on_day
    FROM fact_sales fs
    JOIN dim_date dd ON dd.d LIKE 'd_%'  -- Match valid day columns
    WHERE dd.event_name_1 IS NOT NULL AND dd.event_name_1 != ''  -- Filter for event days
)
SELECT
    store_id,
    event_name_1,
    SUM(sell_price * sales_on_day) AS total_sales
FROM SalesByDay
WHERE sales_on_day > 0  -- Filter for actual sales
GROUP BY event_name_1
ORDER BY total_sales DESC;



